import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { Link, useNavigate } from 'react-router-dom';
import { addNote } from '../Redux/Action';
import { NoteAdded } from './PopUp';


export default function Notes() {

    let [title, setTitle] = useState('');
    let [content, setContent] = useState('');
    let [noteAdded,setNoteAdded]=useState(false)

    const dispatch = useDispatch();

    const navigate = useNavigate();

    function handleSubmission(e) {
        e.preventDefault();
        dispatch(addNote(title, content))
        setTitle('')
        setContent('')

        navigate('/allNotes')

        setNoteAdded(true);
            setTimeout(()=>{
                setNoteAdded(false)
            },500)
        

    }

    return (
        <>
          
            <div className='container'>
                <div className='row'>
                    <div className='col-12' >
                        <div className='Note'>
                        <form className='form' onSubmit={handleSubmission}>
                            <h2>Add A Note</h2>
                            <input type='text' name='text' value={title} onChange={(e) => setTitle(e.target.value)} placeholder='Enter Name' />
                            <br />
                            <input type='text' name='content' value={content} onChange={(e) => setContent(e.target.value)} placeholder='Enter Number' />
                            <br />
                            <button type='submit'> Add Note  </button>
                        </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
