import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { addNote } from '../Redux/Action';


export default function Notes() {

    let [title, setTitle] = useState('');
    let [content, setContent] = useState('');

    const dispatch = useDispatch();

    function handleSubmission(e) {
        e.preventDefault();
        dispatch(addNote(title, content))
        setTitle('')
        setContent('')

    }

    return (
        <>
            <div className='container'>
                <div className='row'>
                <h3 className='heading'>React redux notes app</h3>
                    <div className='col-12' id='Note'>
                        <form className='form' onSubmit={handleSubmission}>
                           
                            <input type='text' name='text' value={title} onChange={(e) => setTitle(e.target.value)} placeholder='Enter Name' />
                            <br />
                            <input type='text' name='content' value={content} onChange={(e) => setContent(e.target.value)} placeholder='Enter Number' />
                            <br />
                            <button type='submit' >
                                Add Note
                            </button>
                        </form>
                    </div>
                </div>
            </div>



        </>
    )
}
