import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { removeNote } from '../Redux/Action';
import { useNavigate } from 'react-router-dom';

export default function AllNotes() {

    const notes = useSelector((state) => state.notes)
    const dispatch = useDispatch();
    const navigate = useNavigate();
    return (
        <div>
            {
                notes.map((notes, index) => {
                    return (
                        <>
                            <div className='container'>
                                <div className='row'>
                                <button className="button" role="button" onClick={() => navigate('/')}>Home</button>
                                    <div className='col-12' id='allNotes'>
                                        <table>
                                            <tr>
                                             <th>Sr.No</th>
                                             <th>Name</th>
                                             <th>Number</th>
                                            </tr>
                                            <tr>
                                            <td>{index + 1 }</td>
                                            <td>{notes.title}</td>
                                            <td>{notes.content} </td>
                                            <a href='#' className='btn btn-danger' onClick={()=>dispatch(removeNote(index))}>Close</a>
                                            </tr>
                                        </table>
                                    </div>
                                </div>
                            </div>

                        </>

                    )
                })
            }
        </div>
    )
}
